(5, 1, 'Terms &amp; Conditions', '&lt;p align=&quot;right&quot;&gt;\r\n\r\n\r\n	\r\n	\r\n	\r\n	&lt;style type=&quot;text/css&quot;&gt;p { margin-bottom: 0.1in; line-height: 115%; text-align: right; }a:link { }&lt;/style&gt;\r\n\r\n\r\n&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 0in; line-height: 100%&quot;&gt;&lt;br&gt;\r\n\r\n&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 0in; line-height: 100%&quot;&gt;&lt;font color=&quot;#680059&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;font style=&quot;font-size: 16pt&quot; size=&quot;4&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;b&gt;البيع\r\nعلي ميجو استور&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;font style=&quot;font-size: 16pt&quot; size=&quot;4&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;b&gt;.&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;font style=&quot;font-size: 16pt&quot; size=&quot;4&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;b&gt;كوم&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 0in; line-height: 100%&quot;&gt; \r\n&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;تقديم&lt;/strong&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;b&gt;ميجو&lt;/b&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;b&gt;-&lt;/b&gt;&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;b&gt;استور&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;b&gt;.&lt;/b&gt;&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;b&gt;كوم&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\nيوفر &lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;بيئه&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\nعمل لتسهيل عمليات التجارة الإلكترونية\r\nفي البيع والشراء &lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;وزياده\r\nالمبيعات من خلال&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\nالموقع&lt;/span&gt;&lt;/strong&gt;&lt;/font&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;.\r\n&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;ويوفر\r\nلأصحاب المحلات التجاريه طرق الشحن\r\nوالتحصيل النقدي من العملاء &lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;الدفع\r\nاونلاين&lt;/span&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;)\r\n&lt;/span&gt;&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\n&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;داخل\r\nالمملكه العربيه السعوديه &lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;.&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;الأحكام\r\nوالشروط &lt;/strong&gt;&lt;/span&gt;&lt;/font&gt;\r\n&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;من فضلك قم بقراءة\r\nالشروط والأحكام التالية بعناية قبل\r\nالبدء في استخدام هذا الموقع الالكتروني\r\n&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;.&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;ol&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أبرمت هذه\r\n	الشروط والأحكام من جانب مستخدم هذا\r\n	الموقع و &lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;b&gt;ميجو&lt;/b&gt;&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;b&gt;-&lt;/b&gt;&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;b&gt;استور&lt;/b&gt;&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;b&gt;.&lt;/b&gt;&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;b&gt;كوم&lt;/b&gt;&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;من أجل &lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;البيع\r\n	خلال &lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;هذا الموقع &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(\r\n	mego-store.com )  &lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;يشمل\r\n	جميع نطاقاته الفرعية وروابطه وصفحاته\r\n	الإلكترونية&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;في شروط\r\n	الموقع هذه، تعني كلمات &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	و &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;نحن&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	و&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ضمير الجماعة\r\n	المتكلم&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt; و&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ملكنا&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;،\r\n	حسبما ينطبق، كل من أو جميع طرف &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أطراف&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	المتعاقدة &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;حسب\r\n	ما هو منصوص عليه أدناه لكل دولة مختارة\r\n	مشار إليها أدناه&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أو أي\r\n	من الشركات التابعة لها&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وتعني\r\n	كلمة “&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;شركة تابعة&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;”\r\n	فيما يتعلق بأي كيان، أي كيان آخر أو شخص\r\n	آخر يسيطر بشكل مباشر أو غير مباشر على\r\n	ذلك الكيان أو خاضع له أو خاضع للسيطرة\r\n	المشتركة لذلك الكيان&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وتعني\r\n	كلمة “&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;شخص&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;”\r\n	أي فرد أو شركة أو شراكة أو شركة ذات\r\n	مسؤولية محدودة أو جهة حكومية أو جمعية\r\n	أو مشروع مشترك أو إدارة أو كيان محدد،\r\n	سواء كان له وجود قانوني واضح أم لا&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;أنت تتفهم\r\n	وتوافق على أن الموقع من حقه وفقا لتقديره\r\n	الذاتي أن يقوم بعكس هذه الحقوق &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;بدون\r\n	إبداء أي التزامات&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;وأن يقوم\r\n	بإنهاء هذه الشروط والأحكام، ويمنعك من\r\n	الاستخدام المستقبلي لهذا الموقع أو أن\r\n	يقوم باتخاذ فعل قانوني ضدك، إذا قمت\r\n	بخرق هذه الشروط والأحكام، أو قمت باستخدام\r\n	الأنشطة ممنوعة والغير مقبول التالية&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;.&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;إنشاء\r\n	حساب بائع &lt;/font&gt;&lt;/strong&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;داخل\r\n	المملكه العربيه السعوديه &lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/font&gt;&lt;strong&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;مجانا&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;حسب\r\n	التعليمات الواردة في الموقع، يتعين\r\n	عليك إنشاء حساب بائع لدينا  فرداً، يجب\r\n	أن تكون مقيماً في &lt;/span&gt;&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\n	المملكه العربيه السعوديه&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\n	، أما في حالة الشركات، يتعين أن يكون\r\n	لدى الشركة القدرة على ممارسة الأعمال\r\n	في &lt;/span&gt;&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\n	المملكه العربيه السعوديه&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\n	حسب القوانين المعمول بها في &lt;/span&gt;&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\n	المملكه العربيه السعوديه&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;.\r\n	&lt;/span&gt;&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;\r\n	&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;b&gt;يجب عدم\r\n	التسجيل باسم مستعار&lt;/b&gt; و&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;/&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;أو\r\n	انتحال بيانات اعتماد تسجيل الدخول\r\n	الخاصة بأي بائع آخر أو كلمة &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;كلمات&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;السر\r\n	الخاصة به، حيث ينتج عن هذا السلوك\r\n	الاحتيالي مخالفة القوانين واللوائح\r\n	الاتحادية أو الوطنية&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;.\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;كما أنكم\r\n	توافقون على تقديم كافة المعلومات ذات\r\n	الصلة إلينا طالما كان حسابكم فعالاً&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;.\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;ويجب عليكم\r\n	عند تسجيل حساب البائع الخاص بكم،  تقديم\r\n	الوثائق الثبوتية السارية والأصلية التي\r\n	نحددها وذلك حسب ما نطلبه من خلال الموقع\r\n	أو حساب البائع الخاص بكم&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;.&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;كلمات\r\n	السر&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;في\r\n	حالة اختياركم أو تم تزويدكم باسم مستخدم\r\n	أو كلمة سر أو أية معلومة أخرى كجزء من\r\n	إجراءاتنا الأمنية، يجب عليكم التعامل\r\n	مع هذه المعلومات بسرية تامة، حيث تتحملون\r\n	المسؤولية وحدكم عن الحفاظ على سلامة\r\n	كلمة السر الخاصة بكم&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ولا\r\n	يجوز لكم الإفصاح عن كلمة السر للغير\r\n	&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;بخلاف\r\n	الأطراف الأخرى المفوضة من طرفكم لاستخدام\r\n	حسابكم، كما تتحملون المسؤولية وحدكم\r\n	عن أي استخدام أو عمل متخذ بموجب كلمة\r\n	السر الخاصة بكم&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وفي\r\n	حالة اختراق كلمة السر الخاصة بكم، يجب\r\n	عليكم تغييرها على الفور&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويحق\r\n	لنا إيقاف أي اسم تعريف أو كلمة سر، سواء\r\n	تم اختيارها من طرفكم، أو قمنا نحن\r\n	بتخصيصها، في أي وقت إذا ارتئينا حسب\r\n	رأينا المعقول أنكم قد أخفقتم في الالتزام\r\n	بأي من شروط الموقع هذه&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;b&gt;يكون طرف\r\n	&lt;/b&gt;&lt;b&gt;ميجو&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;b&gt;-&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;b&gt;استور&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;b&gt;.&lt;/b&gt;&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;b&gt;كوم&lt;/b&gt;&lt;b&gt;\r\n	المتعاقد&lt;/b&gt; هو شركه الأنامل العجيبه\r\n	لتقنيه المعلومات وهي شركة ذات مسؤولية\r\n	محدودة تأسست بموجب قوانين المملكة\r\n	العربية السعودية ورقم تسجيلها التجاري\r\n	هو &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;1010434700\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;ومقرها \r\n	في برج الحقيط طريق الملك عبد العزيز آل\r\n	سعود، الخبر ، المملكة العربية السعودية\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;.&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;واجبات\r\n	البائع&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;يجوز\r\n	لكم فقط الاستفادة من الموقع لأغراض\r\n	مشروعة وبصورة قانونية&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويجب\r\n	عليكم الالتزام بجميع القوانين المعمول\r\n	بها في&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span style=&quot;font-weight: normal&quot;&gt;\r\n	المملكه العربيه السعوديه&lt;/span&gt;&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;..\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويتعين\r\n	عليكم ضمان توافق منتجاتكم مع الصور\r\n	الموضوعة على الموقع ومع المعلومات\r\n	المقدمة إلينا، ويجب عليكم الابتعاد عن\r\n	وضع أي إعلان أو معلومات مضللة عن منتجاتكم\r\n	أو عن أية علامة تجارية على الموقع&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وفي\r\n	حالة اكتشاف مخالفتكم لشروط الموقع هذه،\r\n	يجب عليكم قبول إرجاع المنتجات وتحمل\r\n	المسؤولية أمامنا وفقاً لشروط الموقع\r\n	هذه ، ويجب أن تلتزم المنتجات المعروضة\r\n	للبيع على الموقع بجميع القوانين واللوائح\r\n	وسياسات وبروتوكولات &lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;b&gt;ميجو&lt;/b&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	التي يتم تحديدها في الموقع وتحديثها من\r\n	حين لآخر&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويمنع\r\n	منعاً باتاً بيع منتجات غير قانونية أو\r\n	غير آمنة أو غير ذلك من المنتجات المحظورة\r\n	الواردة في هذه الصفحات، بما في ذلك\r\n	المنتجات التي تتاح فقط بموجب وصفة طبية&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وينبغي\r\n	عليكم قبل إدراج أو الطلب منا إدراج\r\n	المنتجات إلى الموقع&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;عملية\r\n	البيع وتنفيذها&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;بخلاف\r\n	الحالات التي نوافق فيها على تقديم\r\n	خدماتنا إليكم حسب شروط خدماتنا الأخرى\r\n	&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وفي\r\n	هذا الوقت فقط حسب الضمانات وبالقدر\r\n	المنصوص عليه في شروط الخدمات الأخرى\r\n	المذكورة&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;،\r\n	وبالنسبة لأي من خدماتنا التي تطلبون\r\n	استخدامها أو تستفيدون منها &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;والتي\r\n	نوافق على تقديمها&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;في كل\r\n	دولة من الدول المختارة، يتعين عليكم&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n&lt;/li&gt;&lt;/ol&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;ضمان عدم\r\n	تسبب منتجاتكم في حدوث أية إصابة شخصية\r\n	أو تلف بالممتلكات&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;أن تقدموا\r\n	إلى ميجو &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;عند\r\n	الطلب&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;نسخة محدثة\r\n	من رخصتكم التجارية &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;في\r\n	حالة الشركات&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;أو نسخة\r\n	من جواز سفركم أو هويتكم الوطنية &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;في\r\n	حالة الأشخاص الطبيعيين&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;تقديم\r\n	الضمانات المطلوبة والصيانة الخاصة\r\n	بالمنتجات التي تم شراؤها وفقاً لممارسات\r\n	السوق وإرشادات وبروتوكولات &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	والقانون &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;القوانين&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;المعمول\r\n	به&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ا&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;؛د&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;الاستمرار\r\n	في تحمل مسؤولية خدمات ما بعد البيع\r\n	والكفالات والضمانات والصيانة وأي عيوب\r\n	قد تظهر في أو فيما يتعلق بمنتجاتكم، حسب\r\n	القانون المعمول به &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;حيث\r\n	يجوز لكم أن تطلبوا منا القيام بذلك نيابة\r\n	عنكم&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;؛هـ&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;جلب\r\n	وعرض وبيع وإنجاز منتجاتكم، في كل حالة،\r\n	حسب شروط الطلبية وبيانات الشحن &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وذلك\r\n	بخصوص أي من منتجاتكم التي يتم طلبها من\r\n	خلال الموقع&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;،\r\n	وشروط الموقع هذه، وجميع الشروط المقدمة\r\n	من قبلكم &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;بموافقتنا&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أو من\r\n	قبلنا والتي يتم عرضها على الموقع في وقت\r\n	الطلبية، حيث تتحملون المسؤولية بمفردكم\r\n	عن وتتحملون جميع مخاطر تلك الأنشطة&lt;/font&gt;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;تنفيذ طلبات\r\n	منتجاتكم باستخدام احدى شركات الشحن\r\n	المعتمدة لدينا &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;والتي\r\n	سيتم إبلاغكم عنها بالبيانات المقدمة\r\n	من خلال حساب البائع الخاص بك&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;تعبئة كل\r\n	منتج من منتجاتكم بصورة تجارية معقولة\r\n	بحيث تتوافق مع جميع متطلبات التعبئة\r\n	والملصقات&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;استرجاع\r\n	بيانات الطلبية ومعلومات الشحن الخاصة\r\n	بأي من منتجاتكم التي تم طلبها من خلال\r\n	الموقع للامتثال لالتزاماتكم وفقا لشروط\r\n	الموقع هذه&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;تزويد ميجو\r\n	بالمعلومات اللازمة بخصوص تنفيذ الطلبات\r\n	ووضع الطلبية وتتبعها &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;بالقدر\r\n	المتاح&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;،\r\n	في كل حالة من الحالات حسب ما نطلبه\r\n	باستخدام العمليات المحددة من قبلنا،\r\n	ويكون من حقنا إتاحة أي من هذه المعلومات\r\n	علناً&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;الالتزام\r\n	بجميع التعليمات، إن وجدت، التي تحددها\r\n	الجهة المصنعة، الموزع، و&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;/&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;أو\r\n	جهة ترخيص المنتج، والتي تحدد التاريخ\r\n	الذي لا يجوز قبله إتاحة المعلومات\r\n	المحددة عن ذلك المنتج &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;مثال&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;:\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;عنوان\r\n	كتاب&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;،\r\n	أو الذي لا يجوز قبله تسليم المنتج أو\r\n	خلافاً لذلك إتاحته للعملاء&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;أن تكونوا\r\n	البائع لكل منتج من منتجاتكم وعدم التصرف\r\n	نيابة عن أي شخص آخر&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;إرفاق بطاقة\r\n	تعبئة مخصصة للطلبية، وإذا كان ينطبق،\r\n	أي فواتير ضريبية، في كل عملية شحن\r\n	لمنتجاتكم&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt; &lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;الالتزام\r\n	بعدم الاتصال بالعملاء &lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;(&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;سواء\r\n	عن طريق الهاتف أو البريد الالكتروني أو\r\n	غير ذلك من وسائل الاتصال&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;)\r\n	&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;ar-EG&quot;&gt;لتأكيد\r\n	الطلبيات أو لتنفيذ منتجاتكم أو لأي سبب\r\n	آخر&lt;/span&gt;&lt;/font&gt;&lt;font face=&quot;Tajawal&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;br&gt;\r\n&lt;/span&gt;&lt;/font&gt;&lt;br&gt;\r\n&lt;br&gt;\r\n\r\n	&lt;/p&gt;\r\n&lt;/li&gt;&lt;/ul&gt;&lt;p align=&quot;right&quot;&gt;\r\n&lt;br&gt;&lt;/p&gt;&lt;ol start=&quot;21&quot;&gt;&lt;li&gt;\r\n&lt;p&gt;  &lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ضمانات\r\n	وإقرارات البائع&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;يقر\r\n	البائع ويضمن أنه &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(1)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;إذا\r\n	كنت شركة، فإنها تأسست وتعمل أصولاً\r\n	وأنها في وضع جيد حسب القوانين المعمول\r\n	بها في المملكه العربيه السعوديه التي\r\n	سجلت فيها حساب البائع الخاص بها، وأنها\r\n	تقوم بالتسجيل للحصول على خدماتنا المقدمة\r\n	بموجب شروط الموقع هذه؛ &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(2)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;لدى\r\n	البائع كل الصلاحية اللازمة والسلطة\r\n	لإبرام شروط الموقع هذه وتنفيذ التزاماته\r\n	ومنح الحقوق والتراخيص والتفويضات في\r\n	شروط الموقع هذه؛ &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(3)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;لدى\r\n	البائع الصلاحية والتراخيص والتصاريح\r\n	والتفويضات وحقوق الملكية والموافقات\r\n	والأذون الخاصة بالمنتجات وأنه يمكنه\r\n	بيع وترويج المنتجات في الإقليم الجغرافي\r\n	التي تباع فيها من خلال الموقع؛ &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(4)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;تتوافق\r\n	منتجات البائع مع معايير الجودة والسلامة\r\n	المعمول بهما؛ &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(5)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;البائع\r\n	مسؤول بمفرده عن أي منتجات يتم طلبها أو\r\n	توزيعها عبر الموقع ويكون مسؤولاً كذلك\r\n	عن أية مخالفة تقع لأي عقود مع الغير؛\r\n	&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(6)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;لا\r\n	يخالف إجراء الطلب وبيع منتجات البائع\r\n	من خلال الموقع أي قوانين معمول بها في\r\n	المملكه العربيه السعوديه أو حقوق الغير؛\r\n	&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(7)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;جميع\r\n	المنتجات بما في ذلك المواد الموردة إلى\r\n	ميجو أصلية وخالية من أي عيوب؛ &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(8)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;لن\r\n	ينخرط البائع في ممارسات تجارية غير\r\n	عادلة و&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;/&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أو\r\n	ينشر أي دعاية أو معلومات غير دقيقة أو\r\n	مضللة عن المنتجات عبر الموقع؛ &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(9)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;لن\r\n	يقوم البائع ببيع أو تسليم العميل أي\r\n	بنود مجددة أو مستعملة وأن المنتجات\r\n	ستكون دائماً جديدة وغير مستعملة؛ و&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(10)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;يفهم\r\n	البائع التزامات حماية المستهلكين\r\n	التابعين له كمورد ويؤمن &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو\r\n	&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt; ضد أي خسائر أو\r\n	تكاليف تتكبدها &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	فيما يتعلق بالتزامات البائع بموجب أي\r\n	قوانين معمول بها في المملكه العربيه\r\n	السعوديه&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;المرتجعات&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;يتعهد\r\n	البائع بالالتزام بسياسة الارتجاع وإعادة\r\n	الثمن المنصوص عليها في الموقع، على\r\n	نفقته الخاصة حيث تكون المنتجات المباعة\r\n	عبر الموقع خاضعة للإرجاع أو إعادة ثمنها\r\n	خلال فترة الارتجاع وإعادة الثمن من\r\n	تاريخ تسليم المنتجات للعميل&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وتسري\r\n	سياسة الإرجاع طالما كانت المنتجات\r\n	موجودة في عبواتها الأصلية وأعيدت إلى\r\n	البائع بحالتها الأصلية التي كانت عليها\r\n	وقت الشراء والاستلام&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وسيقوم\r\n	فريق دعم العملاء لدينا بترتيب عملية\r\n	الإرجاع إلى البائع من خلال حجز موعد لدى\r\n	ساعي البريد لاستلام المنتج وإعادته إلى\r\n	البائع بما يتوافق مع السياسة، وفي هذه\r\n	المرحلة يتعين على البائع إعادة المبلغ\r\n	كاملاً إلى العميل المذكور&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ومع\r\n	مراعاة أحكام هذا البند، يجب على البائع\r\n	دائماً قبول إعادة المنتج&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;إذا\r\n	كان إرجاع المنتج غير قائم على ادعاء\r\n	مخالفة البائع لأي من التزاماته، لن يقوم\r\n	&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	باحتساب أية تكاليف أو غرامات &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ما\r\n	لم ينص على خلاف ذلك في شروط الموقع هذه&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;).\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;سنقوم\r\n	إما أن نعيد المنتجات إلى عنوان الشحن\r\n	الخاص بكم&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;إذا\r\n	قمنا بترتيب عملية الإرجاع وإذا &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أ&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;تبين\r\n	أن عنوان الشحن الخاص بكم والموجود لدينا\r\n	قديم أو غير صحيح؛ &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ب&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;لم\r\n	تقوموا بتقديم عنوان شحن خاص في المملكه\r\n	العربيه السعوديه؛ أو &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ج&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;لم\r\n	نستطع القيام بالترتيبات اللازمة لكم\r\n	لسداد قيمة الشحنة المرتجعة، ففي هذه\r\n	الحالة سنعتبر أنكم قد تنازلتم عن\r\n	المنتج&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;المنتجات&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويجوز\r\n	لنا  التصرف فيها&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويجب\r\n	عليكم تعويضنا عن جميع التكاليف والمصاريف\r\n	التي تكبدناها نظير تخزين وإرجاع أو\r\n	التصرف في أي من المنتجات&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويجوز\r\n	لنا، إن اخترنا ذلك، التصرف في أي من\r\n	المنتجات &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وسنعتبر\r\n	أنكم قد وافقتم على تصرفنا&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;على\r\n	الفور إذا قررنا وفق تقديرنا المطلق أن\r\n	تلك المنتجات تمثل خطراً على السلامة أو\r\n	الصحة أو تنشأ التزاماً على ميجو أو\r\n	موظفيها أو أي من الغير&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وبالقدر\r\n	الذي نختار فيه التصرف في أي من تلك\r\n	المنتجات، تنتقل إلينا ملكية كل منتج من\r\n	المنتجات التي تم التصرف فيها دون مقابل\r\n	وذلك بالقدر الذي يسمح لنا التصرف في\r\n	المنتج&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;المنتجات&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;،\r\n	وسوف نحتفظ بجميع العائدات، إن وجدت،\r\n	التي نحصل عليها جراء هذا التصرف&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;التزام\r\n	ميجو&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;strong&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;-&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;استور&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;كوم&lt;/font&gt;&lt;/span&gt;&lt;/strong&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;يعتبر\r\n	الموقع وأي من خدماتنا المقدمة من خلاله\r\n	أو أي من الخصائص المستخدمة أو القائمة\r\n	عليه بما في ذلك كل المحتوى والبرمجيات\r\n	والوظائف والمواد والمعلومات المتاحة\r\n	بخصوص أو مقدمة فيما يتصل بخدماتنا أنها\r\n	مقدمة “كما هي”&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويعتبر\r\n	كل كيان من كيانات ميجو أو أي شخص مسؤولاً\r\n	بشكل منفرد عن التزاماته الخاصة بموجب\r\n	شروط الموقع هذه وليسوا مسؤولين مجتمعين\r\n	عن الالتزامات الخاصة بأي كيان أو شخص\r\n	آخر تابع ل &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	بموجب شروط الموقع هذه&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وبصفته\r\n	مستخدماً للموقع، يقوم البائع باستعمال\r\n	الموقع وخدماتنا المقدمة إلى البائع على\r\n	مسؤوليته الخاصة&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;وبحسب\r\n	أقصى قدر يسمح به القانون، تخلي ميجو\r\n	والشركات التابعة مسؤوليتها عما يلي&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	(1) &lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أي\r\n	إقرارات أو ضمانات تتعلق بشروط الموقع\r\n	الواردة هذه، أو أي من خدماتنا أو التعاملات\r\n	المنصوص عليها في شروط الموقع بما في ذلك\r\n	أي ضمانات ضمنية تتعلق بالرواج التجاري\r\n	أو الصلاحية لغرض معين أو عدم الانتهاك؛\r\n	&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(2)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;الضمانات\r\n	الضمنية التي تنشأ خلال التعامل أو\r\n	التنفيذ أو استخدام هذا النوع من التجارة؛\r\n	و &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(3)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أي\r\n	التزام أو مسؤولية أو حق أو مطالبة أو\r\n	وسيلة للتعويض عن الضرر، سواء كان ناتج\r\n	عن الإهمال من طرفنا أم لا&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ولا\r\n	تضمن &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو &lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	أن الخصائص الموجودة على الموقع وفي\r\n	خدماتنا سوف تتوافق مع متطلبات البائع\r\n	أو أن تكون متاحة أو موجودة في الوقت\r\n	المناسب أو آمنة أو غير منقطعة أو خالية\r\n	من الأخطاء، ولن تكون ميجو مسؤولة عن أي\r\n	انقطاع في الخدمة بما في ذلك على سبيل\r\n	المثال لا الحصر أعطال النظام أو غير ذلك\r\n	من الانقطاعات التي قد تؤثر على استلام\r\n	أو معالجة أو قبول أو إنجاز أو تسوية أي\r\n	معاملات&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;كما\r\n	لا تنخرط ميجو في أية معاملات بين العملاء\r\n	والبائعين أو غير ذلك من التعاملات وذلك\r\n	في حال نشوب نزاع بين واحد أو أكثر من\r\n	المشاركين، وعليه يعفي كل مشارك من\r\n	المشاركين ميجو والشركات التابعة لها\r\n	&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ووكلائها\r\n	وموظفيها&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;من\r\n	أية مطالبات وطلبات وتعويضات &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;فعلية\r\n	أو تبعية&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;أياً\r\n	كان نوعها أو طبيعتها وسواء كانت معلومة\r\n	أم غير معلومة وسواء كانت محل شك أم لا\r\n	وسواء كان قد أفصح عنها أم غير مفصح عنها\r\n	والتي تنشأ عن أو تتصل بأي شكل من الأشكال\r\n	بتلك النزاعات&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;لا\r\n	تتعدى مسؤوليتنا التي تنشأ عن أو فيما\r\n	يتصل بشروط الموقع هذه أو المعاملات\r\n	المنصوص عليها، سواء من الناحية التعاقدية\r\n	أو الضمان أو الضرر &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;بما\r\n	في ذلك الإهمال أو المسؤولية عن المنتج\r\n	أو غير ذلك من النظريات&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;تجاه\r\n	البائع أو أي شخص آخر عن أية تكلفة لتغطية\r\n	أو استرداد أو التعويض عن أي استثمار\r\n	يقوم به البائع أو أي من الشركات التابعة\r\n	له فيما يتصل بشروط الموقع هذه، أو عن\r\n	أية خسارة للربح أو العائدات أو العمل\r\n	أو البيانات أو التعويضات التأديبية أو\r\n	التبعية التي تنشأ عن أو تتعلق بشروط\r\n	الموقع هذه، حتى وإن كانت &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	أو الشركات التابعة لها قد اخطروا باحتمال\r\n	حدوث مثل تلك التكاليف أو الأضرار، لن\r\n	تتعدى في مجملها قيمة تساوي المبالغ\r\n	المدفوعة إلى البائع خلال مدة الستة أشهر\r\n	السابقة فيما يتصل بالخدمة المعينة التي\r\n	كانت سبباً في نشوء هذه المطالبة التي\r\n	سبقت مباشرة الحدث الذي كان أساساً لنشوء\r\n	تلك المسؤولية&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;تخفيف\r\n	الخسائر&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;باستثناء\r\n	ما إذا كان الأمر يتعلق بأية مطالبة أو\r\n	دعوى خاصة بعدم سداد الرسوم، يجب على كل\r\n	طرف اتخاذ جميع الخطوات المعقولة لتخفيف\r\n	الخسائر والأضرار التي يتكبدها جراء أية\r\n	مطالبة أو دعوى &lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;(&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;سواء\r\n	كان ذلك بسبب الإهمال أو مخالفة العقد\r\n	أو الإدلاء ببيانات غير صحيحة، بموجب أي\r\n	تعويض أو خلاف ذلك&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;)\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;يقيمها\r\n	ضد الطرف الآخر&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;الإفصاح\r\n	عن المعلومات&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;تحتفظ\r\n	&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	بحقها في الإبلاغ عن أي نشاط إذا شكت أنه\r\n	يخالف أي قانون من القوانين المعمول بها،\r\n	وذلك إلى مسؤولي تنفيذ القانون المعنيين\r\n	أو الجهات الرقابية أو الغير&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ولأجل\r\n	التعاون مع الطلبات الحكومية لحماية\r\n	&lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو &lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	وعملائها أو لضمان نزاهة سير أعمال &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	وأنظمتها، يجوز ل &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	الدخول إلى والإفصاح عن أي معلومات تراها\r\n	ضرورية أو مناسبة، بما في ذلك على سبيل\r\n	المثال لا الحصر بيانات حساب البائع\r\n	وبيانات الاتصال الخاصة به وعنوان\r\n	بروتوكول الإنترنت ومعلومات مرور البيانات\r\n	وتاريخ الاستخدام والمحتوى المنشور&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;ويجب\r\n	على كل من &lt;/font&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;ميجو&lt;/font&gt;&lt;/strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;\r\n	والبائع حماية بيانات العملاء حسب سياسات\r\n	كل منهم والقوانين المعمول بها في المملكه\r\n	العربيه السعوديه&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;/p&gt;\r\n	&lt;/li&gt;&lt;li&gt;\r\n&lt;p&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;strong&gt;&lt;font face=&quot;Tajawal&quot;&gt;المدة\r\n	والإنهاء&lt;/font&gt;&lt;/strong&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;:\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;ar-EG&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;تبدأ\r\n	شروط الموقع هذه اعتباراً من تاريخ إنشاء\r\n	حسابكم أو توقيعكم عقد كتابي معنا وتظل\r\n	مستمرة ما لم وحتى يتم إنهائها من قبلنا\r\n	أو من طرفكم&lt;/font&gt;&lt;/span&gt;&lt;font face=&quot;Liberation Serif, serif&quot;&gt;&lt;span lang=&quot;en-US&quot;&gt;&lt;font face=&quot;Tajawal&quot;&gt;.\r\n	&lt;/font&gt;&lt;/span&gt;&lt;/font&gt;&lt;span lang=&quot;a